import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import { useState, useEffect } from "react";
import MyAccordin from "./MyAccordin";
import Footer from "./Footer";
import Navbar from "react-bootstrap/Navbar";
import Silv from "./SilvProject";
import { Link } from "react-router-dom";
import { BrowserRouter, Switch, Route, Routes } from "react-router-dom";
function ColorSchemesExample() {
  const [activeLink, setActiveLink] = useState("home");
  return (
    <>
      <BrowserRouter>
        <Navbar sticky="top" className="myBlackDarkBg myTextWhite shadow">
          <Container>
            <Navbar.Brand href="#home" className="fs-1 fw-bolder myTextWhite">
              <h1>Leonardo Antonelli </h1>
            </Navbar.Brand>
            <Nav className="me-auto fs-4  ">
              <Nav.Link
                href="#home"
                className={
                  activeLink === "home" ? "active navbar-link" : "navbar-link"
                }
                onClick={() => onUpdateActiveLink("home")}
              >
                Home
              </Nav.Link>
              <Nav.Link
                href="#skills"
                className={
                  activeLink === "skills" ? "active navbar-link" : "navbar-link"
                }
                onClick={() => onUpdateActiveLink("skills")}
              >
                Skills
              </Nav.Link>
              <Nav.Link
                href="#projects"
                className={
                  activeLink === "projects"
                    ? "active navbar-link"
                    : "navbar-link"
                }
                onClick={() => onUpdateActiveLink("projects")}
              >
                Projects
              </Nav.Link>

              <Link
                href="#skills"
                className=" myTextWhite .pheader myTextWhite"
              >
                Skills
              </Link>
              <Link href="#projects">
                <h5 className=".pheader  myTextWhite">Projects</h5>
              </Link>
              <Link href="#contact">
                {" "}
                <h5 className=".pheader  myTextWhite">Contact</h5>
              </Link>
            </Nav>
          </Container>
        </Navbar>

        <Routes>
          <Route path="/skills" element={<MyAccordin />} />
          <Route path="/contatti" element={<Footer />} />
          <Route path="/projects" element={<Silv />} />
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default ColorSchemesExample;
