import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import { useState, useEffect } from "react";
import MyAccordin from "./MyAccordin";
import Footer from "./Footer";
import Navbar from "react-bootstrap/Navbar";
import Silv from "./SilvProject";
import { Link } from "react-router-dom";
import { BrowserRouter, Switch, Route, Routes } from "react-router-dom";
function ColorSchemesExample() {
  const [activeLink, setActiveLink] = useState("home");

  const onUpdateActiveLink = (value) => {
    setActiveLink(value);
  };

  return (
    <>
      <BrowserRouter>
        <Navbar sticky="top" className="myBlackDarkBg myTextWhite shadow">
          <Container>
            <Navbar.Brand href="#home" className="fs-1 fw-bolder myTextWhite">
              <h1>Leonardo Antonelli </h1>
            </Navbar.Brand>
            <Nav.L className="me-auto fs-4  ">
              <Link
                href="#skills"
                className=" myTextWhite .pheader myTextWhite"
                onClick={() => onUpdateActiveLink("skills")}
              >
                Skills
              </Link>
              <Link href="#projects">
                <h5 className=".pheader  myTextWhite">Projects</h5>
              </Link>
              <Link href="#contact">
                {" "}
                <h5 className=".pheader  myTextWhite">Contact</h5>
              </Link>
            </Nav.L>
          </Container>
        </Navbar>

        <Routes>
          <Route path="/skills" element={<MyAccordin />} />
          <Route path="/contatti" element={<Footer />} />
          <Route path="/projects" element={<Silv />} />
        </Routes>
      </BrowserRouter>
    </>
  );
}

export default ColorSchemesExample;
